mapper.select:
SELECT * 
FROM AREA 
WHERE GU IS NOT NULL AND DONG IS NOT NULL



	AND SI =#{si}



	AND SI =#{si} AND GU =#{gu}



	AND SI =#{si} AND GU =#{gu} AND DONG = #{dong}

OFFSET #{page} ROWS FETCH NEXT #{pageSize} ROWS ONLY

SELECT COUNT(*)
FROM AREA
WHERE GU IS NOT NULL AND DONG IS NOT NULL 



	AND SI =#{si}



	AND SI =#{si} AND GU =#{gu}



	AND SI =#{si} AND GU =#{gu} AND DONG = #{dong}
