mapper.select:
SELECT *
FROM TBL_USER
WHERE USERID = #{id} AND NAME = #{name} AND REPLACE(PHONE,'-','') = #{phone}

SELECT U.*, DECODE( BIRTH,null,'정보없음', SUBSTR(BIRTH,3,2) || '.' || SUBSTR(BIRTH,5,2) || '.' || SUBSTR(BIRTH,7,2) ) CBIRTH FROM TBL_USER U WHERE NAME LIKE '%' || #{keyword} || '%' OR NICKNAME LIKE '%' || #{keyword} || '%'
AND USERID = #{id}
OFFSET #{page} ROWS FETCH NEXT #{pageSize} ROWS ONLY

mapper.update:

UPDATE TBL_USER
SET CNT = CNT + 1	
WHERE USERID = #{id}

UPDATE TBL_USER
SET CNT = 0
WHERE USERID = #{id}

UPDATE TBL_USER
SET PASSWORD = #{pwd}
WHERE USERID = #{id}
